import { useState, useEffect } from 'react';
import './StopSequence.css';

export default function StopSequence({onStopSequenceChange}) {
  const [stopsequence, setStopSequence] = useState(null); // Initial temperature value

  const handleStopSequenceChange = (event) => {
    setStopSequence(event.target.value);
    var stopsequence = document.getElementById("stopsequence").value
    onStopSequenceChange(stopsequence);
  };

  useEffect(() => {
    const textarea = document.getElementById('stopsequence');

    textarea.addEventListener('input', () => {
      textarea.style.height = 'auto';
      textarea.style.height = `${textarea.scrollHeight}px`;
    });
  }, []);

  return (
    <div class="mr-4 text-center">
        <textarea class="w-full leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-md" rows="1" placeholder="Stop Sequence" id="stopsequence"></textarea>
        <p class="text-xs mt-1">Stop Sequence</p>
    </div>
  );
}
