import { useState, useEffect } from 'react';
import './StopSequence.css';

export default function StopSequence({onStopSequenceChange, compare}) {
  const [stopsequence, setStopSequence] = useState(null); // Initial temperature value

  const handleStopSequenceChange = (event) => {
    setStopSequence(event.target.value);
    var stopsequence = document.getElementById(`stopsequence-${compare}`).value
    onStopSequenceChange(stopsequence);
  };

  useEffect(() => {
    const textarea = document.getElementById('stopsequence');

    textarea.addEventListener('input', () => {
      textarea.style.height = 'auto';
      textarea.style.height = `${textarea.scrollHeight}px`;
    });
  }, []);

  return (
    <div class="text-center">
        <div class="relative">
        <textarea class=" w-40 px-4 py-2 pr-10 leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-sm" placeholder="Stop Sequence" id="stopsequence"></textarea>
        <button class="absolute inset-y-0 right-0 px-3 py-2 text-gray-500" onClick={handleStopSequenceChange}>
            <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
                <line x1="5" y1="12" x2="19" y2="12" />
                <polyline points="12 5 19 12 12 19" />
            </svg>
        </button>
        </div>
        <p class="text-sm">Stop Sequence</p>
    </div>
  );
}
