import React, { useEffect, useState } from 'react';
import TemperatureSlider from './TemperatureControl';
import MaxToken from './MaxToken';
import ModelSelection from './ModelSelection';


const Playground = () => {
    useEffect(() => {
        const textarea = document.getElementById('user-input');

        textarea.addEventListener('input', () => {
          textarea.style.height = 'auto';
          textarea.style.height = `${textarea.scrollHeight}px`;
        });
      }, []);

    useEffect(() => {
        const textarea = document.getElementById('variable');

        textarea.addEventListener('input', () => {
          textarea.style.height = 'auto';
          textarea.style.height = `${textarea.scrollHeight}px`;
        });
      }, []);

    useEffect(() => {
        const textarea = document.getElementById('system-input');

        textarea.addEventListener('input', () => {
          textarea.style.height = 'auto';
          textarea.style.height = `${textarea.scrollHeight}px`;
        });
      }, []);


    const [response, setResponse] = useState(null);
    const [error, setError] = useState(null);

    const [selectedModel, setSelectedModel] = useState(null);


    const makeRequest = async () => {
        const apiKey = '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';
        const url = 'https://api.openai.com/v1/completions';

        try {
          const userInput = document.getElementById('user-input').value;
          const model = "text-davinci-003";
          console.log(model)

          const response = await fetch(url, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${apiKey}`
            },
            body: JSON.stringify({
              model: model,
              prompt: userInput,
              max_tokens: 100
            })
          });

          const data = await response.json();
          setResponse(data.choices[0].text);
        } catch (error) {
          setError('An error occurred while making the request.');
        }
    };


    return (
        <div className="text-black text-lg bg-white">
            <div class="container mx-auto p-4">
                <div class="flex">
                    <div class="w-5/12 pr-2">
                        <div class="bg-white p-4 border-r border-gray-400">
                        <div class="grid grid-cols-12 gap-4">
                            <div class="col-span-2">
                                <p class="text-md text-black">System:</p>
                            </div>
                            <div class="col-span-10">
                                <textarea class="w-full px-4 py-2 leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-sm" id="system-input" placeholder="You are an AI agent that ..."></textarea>
                            </div>
                        </div>
                        <h2 class="text-xl font-bold mb-2 pt-4">
                            User Input
                        </h2>
                        <div class="w-full max-w mx-auto">
                            <div class="relative">
                                <textarea class="w-full px-4 py-2 pr-10 leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-sm" placeholder="Enter input... Place variables between {{ }}" id="user-input"></textarea>
                                <button class="absolute inset-y-0 right-0 px-3 py-2 text-gray-500" onClick={makeRequest}>
                                    <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
                                        <line x1="5" y1="12" x2="19" y2="12" />
                                        <polyline points="12 5 19 12 12 19" />
                                    </svg>
                                </button>
                            </div>
                        </div>
                        <p class="pt-2">Variables</p>
                        <div class="flex pt-3">
                            <div class="mr-4 text-center">
                                <textarea class="w-full leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-md" rows="1" placeholder="" id="variable"></textarea>
                                <p class="text-xs mt-1">Variable 1</p>
                            </div>
                            <div class="mr-4 text-center">
                                <textarea class="w-full leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-md" rows="1" placeholder="" id="variable"></textarea>
                                <p class="text-xs mt-1">Variable 2</p>
                            </div>
                            <div class="text-center">
                                <textarea class="w-full leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden text-md" rows="1" placeholder="" id="variable"></textarea>
                                <p class="text-xs mt-1">Variable 3</p>
                            </div>
                        </div>
                    </div>
                </div>  
                <div className="w-2/12">
                <div className="flex flex-col items-center h-full">
                    <p className="text-center font-bold">Model Tuning</p>
                    <div className="flex flex-col justify-center items-center w-full pt-4">
                    <ModelSelection onModelChange={setSelectedModel}/>
                    <div className="pt-5">
                        <TemperatureSlider />
                    </div>
                    <div className="pt-5">
                        <MaxToken />
                    </div>
                    </div>
                </div>
                </div>
                <div class="w-5/12 pl-2">
                    <div class="p-4 border-l border-gray-400">
                        <h2 class="text-xl font-bold mb-2">Model Output</h2>
                        {response && <p className="mb-4">{response}</p>}
                        {error && <div>{error}</div>}
                    </div>
                </div>
            </div>
        </div>
    </div>
    );
};


export default Playground;
