import React, { useEffect } from 'react';


const Playground = () => {
    useEffect(() => {
        const textarea = document.getElementById('user-input');

        textarea.addEventListener('input', () => {
          textarea.style.height = 'auto';
          textarea.style.height = `${textarea.scrollHeight}px`;
        });
      }, []);

    return (
        <div className="text-black text-lg bg-white">
            <div class="container mx-auto p-4">
                <div class="flex">

                    <div class="w-5/12 pr-2">
                        <div class="bg-white p-4">
                        <h2 class="text-xl font-bold mb-2">Left Section</h2>
                        <p class="mb-4">Display text goes here.</p>
                        <div class="w-full max-w-xs mx-auto">
                            <div class="relative text-gray-600">
                                <input type="text" class="w-full px-4 py-2 pr-8 leading-tight bg-white border border-gray-400 rounded-lg appearance-none focus:outline-none focus:border-blue-500" placeholder="Enter text..." />
                                <div class="absolute inset-y-0 right-0 flex items-center pr-3">
                                <svg class="w-5 h-5 text-gray-400" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
                                    <circle cx="11" cy="11" r="8" />
                                    <line x1="21" y1="21" x2="16.65" y2="16.65" />
                                </svg>
                                </div>
                            </div>
                            </div>
                        </div>
                    </div>


                    <div class="w-2/12"></div>

                    <div class="w-5/12 pl-2">
                        <div class="bg-gray-200 p-4">
                            <h2 class="text-xl font-bold mb-2">Right Section</h2>
                            <p class="mb-4">Display text goes here.</p>
                            <div class="w-full max-w-xs mx-auto">
                                <div class="relative">
                                    <textarea class="w-full px-4 py-2 pr-10 leading-tight bg-white border border-gray-300 rounded-md shadow-md focus:outline-none focus:shadow-outline h-auto resize-none overflow-hidden" placeholder="Enter text..." id="user-input"></textarea>
                                    <button class="absolute inset-y-0 right-0 px-3 py-2 text-gray-500">
                                    <svg class="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
                                        <line x1="5" y1="12" x2="19" y2="12" />
                                        <polyline points="12 5 19 12 12 19" />
                                    </svg>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};


export default Playground;
